// find the square and cube of an number

package monika;

public class SquareCube {

	static void square(int x) {
		System.out.println("the square of number " + x + " is " + x * x);
	}

	static void cube(int x) {
		System.out.println("the cube of number " + x + " is " + x * x * x);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		square(10);
		cube(18);
	}

}
